public static final Function<Integer, Integer> mult(int k)
{
return  x -> x * k;
}

//VARIANT1
//      func(  "A" , "B" , "C" , "D" )
public static <A, B, C, D> String func( A a ,
                                        B b ,
                                        C c ,
                                        D d )
{
return String.format( "%s, %s, %s, %s" , a , b , c , d );
}


//VARIANT2
//      f() .apply( "A" ) .apply( "B" ) .apply( "C" ) .apply( "D" ) 
public static <A, B, C, D> Function<A,Function<B,Function<C,Function<D,String>>>> f()
{
return a -> ( b -> ( c -> ( d -> String.format( "%s, %s, %s, %s", a , b , c , d )  ) ) );
}

