Result<String> rez = Match( i ).of( Case( $( isNull() ) ,
                                           Result.error( "must not be null" ) ) ,
                                     Case( $( String::isBlank ) ,
                                           Result.error( "must not be blank" ) ) ,
                                     Case( $( is( "test" ) ) ,
                                           Result.error( "must not be test" ) ) ,
                                     Case( $() ,
                                           Result.success( "all is success" ) ) );
rez.onSuccess( do1 ).onError( do2 );

