BiFunction<AtomicLong,SynchronousSink<String>,AtomicLong> my_func = ( state , e ) ->
{
long i = state.getAndIncrement();
if( i < 100 )
	{
	e.next( "a" + i );
	}
else
	{
	e.complete();
	}
return state;
};
var v = Flux.generate( AtomicLong::new , my_func , ( state ) -> logAtInfo( "after complete:" + state ) );

v.subscribe( logAtInfoConsumer( "NEXT" ) , logAtInfoConsumer( "ERROR" ) , logAtInfoRunnable( "COMPLETE" ) );

