template.value:
public Mono<String> $method$( final String string )
{
return Mono.justOrEmpty( string )
           .filter( StringUtils::isNotBlank )
           .single()
           .onErrorResume( logAtWarningFunction( "$class$.$method$(empty argument)" , never() ) )
           .map( logAtDebugUnaryOperator( "$class$.$method$" ) )
           .map( this::$method$_ )
           .onErrorContinue( logAtErrorBiConsumer( "$class$.$method$" ) );
}


public String $method$_( final String string )
{
//throw new RuntimeException( " cause " );
return "otvet+" + string;
}

public Mono<String> $method$( final String string )
{
return Mono.justOrEmpty( string )
           .filter( StringUtils::isNotBlank )
           .single()
           .onErrorResume( logAtWarningFunction( "$class$.$method$(empty argument)" , never() ) )
           .map( logAtDebugUnaryOperator( "$class$.$method$" ) )
           .flatMap( this::method2_ );
}


public Mono<String> $method$_( final String string )
{
//throw new RuntimeException( " cause " );
return Mono.justOrEmpty("otvet+" + string);
}
